import React, { Component } from "react";

function sali3Nbi(array, id) {
  let tab = array.splice(id, 1);
  return tab;
}

export default class Todo extends Component {
  state = {
    todos: [
      {
        id: "0",
        name: "chams",
        desc: "lalala",
        state: "not yet"
      },
      {
        id: "1",
        name: "df",
        desc: "ddqfd",
        state: "not yet"
      },

      {
        id: "1",
        name: "df",
        desc: "ddqfd",
        state: "not yet"
      },
      {
        id: "1",
        name: "df",
        desc: "ddqfd",
        state: "not yet"
      }
    ],

    name: "",
    desc: "",
    state: ""
  };

  deleteTodo(e, id) {
    console.log("yddik", id);

    this.setState({
      todos: this.state.todos.filter(todo => todo !== this.state.todos[id])
    });
  }

  addTodo(e) {}

  render() {
    const { todos } = this.state;
    return (
      <div classNameName="App">
        <header>
          <h2>TODO APP</h2>
        </header>

        <div className="main">
          <div className="add-todo">
            <div className="todo-name-field">
              <label for="">name</label>
              <input
                type="text"
                className="name-field"
                placeholder="Todo Name ..."
                name=""
                id=""
              />
            </div>
            <div className="todo-description-field">
              <label for="">Description</label>
              <input
                type="text"
                className="desc-field"
                placeholder="Todo Description ..."
                name=""
                id=""
              />
            </div>

            <div className="todo-state-field">
              <label for="">State</label>
              <input
                type="text"
                className="state-field"
                placeholder="Todo State ..."
                name=""
                id=""
              />
            </div>

            <div className="todo-add-btn">
              <span className="add-btn">ADD</span>
              <span className="edit-btn">Update</span>
            </div>
          </div>

          <div className="list-todo">
            <table className="table">
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Desc</th>
                <th>State</th>
                <th>Remove</th>
                <th>Edit</th>
              </tr>

              {todos.length >= 1 &&
                todos.map((el, id) => {
                  return (
                    <tr>
                      <td>{el.id}</td>
                      <td>{el.name}</td>
                      <td>{el.desc}</td>
                      <td>{el.state}</td>
                      <td
                        className="remove-todo"
                        onClick={e => this.deleteTodo(e, id)}
                      >
                        X
                      </td>
                      <td className="edit-todo">Edit</td>
                    </tr>
                  );
                })}
            </table>
          </div>
        </div>
      </div>
    );
  }
}
